import React, { useEffect, useState } from 'react'
import jwtDecode from 'jwt-decode';
import axios from 'axios';
export default function Home() {

    let token = localStorage.getItem("userToken")
    let baseurl = "https://route-egypt-api.herokuapp.com/";
    let decode = jwtDecode(token)
    let userID = decode._id
    console.log(userID);

    const [notes, setnotes] = useState([])
    async function getAllNote() {
        let { data } = await axios.get(baseurl + "getUserNotes", {
            header: {
                token, userID
            }
        })
        if (data.message == "success") {
            setnotes(data.notes)
        }
        else {
            setnotes([])
        }
    }

    useEffect(() => {
        getAllNote()
    }, [])

    const [note, setnote] = useState({ title: "", desc: "",  userID,token })
    function getNote(e) {
        let mynote = {...note}
        mynote[e.target.name] = e.target.value;
        setnote(mynote)
        console.log(note);
    }

   async function addNote(e) {
    e.preventDefault()
    let {data}=await axios.post( baseurl+"addNote",note) 
    console.log(data);
    if (data.message=="success") {
        getAllNote()
    }
    
    }


    return (
        <>




            <div className="container my-5">
                <div className="col-md-12 text-end">
                    <a className="add p-2 btn" data-bs-toggle="modal" data-bs-target="#exampleModal">
                        <i className="fas fa-plus-circle">
                        </i> Add New</a>
                </div>
            </div>
            {/*====================Modal =========================== */}
                  <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <form onSubmit={addNote}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <div className="modal-body">
                                <input onChange={getNote} placeholder="Type Title" name="title" className="form-control" type="text" />
                                <textarea onChange={getNote} className="form-control my-2" placeholder="Type your note" name="desc" id="" cols="30" rows="10"></textarea>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                <button data-bs-dismiss="modal" type="submit" className="btn btn-info"><i className="fas fa-plus-circle"></i> Add Note</button>
                            </div>
                        </div>
                    </div>
                </form>
            </div>

            {/* <!-- ==========================Notes=============================== --> */}

            <div className="container">
                <div className="row">
                    {notes.map((notew) => {
                        <div className="col-md-4 my-4">
                            <div className="note p-4">
                                <h3 className="float-start">{notew.title}</h3>
                                <a href='#'><i className="fas fa-edit float-end edit"></i></a>
                                <a href='#'> <i className="fas fa-trash-alt float-end px-3 del"></i></a>
                                <span className="clearfix"></span>
                                <p>{notew.desc}</p>
                            </div>
                        </div>

                    })}
                </div>
            </div>



        </>
    )
}
